@props(['quote'])

<div class="auth-image md:flex w-full">
    <div class="w-5/6 z-10">
        <div class="auth-quote-grid">
            <div class="flex flex-rows justify-start col-start-2">
                @for ($i = 0; $i < 5; $i++)
                    <x-filament::icon icon="heroicon-s-star" class="h-4 w-4 text-yellow-500 dark:text-yellow-400" />
                @endfor
            </div>
        </div>
        <div class="auth-quote-grid mt-3">
            <div class="self-start justify-self-end">
                <svg width="34" height="29" viewBox="0 0 34 29" fill="none" xmlns="http://www.w3.org/2000/svg">
                    <path
                        d="M0 14.5V28.25H13.6V14.5H6.8C6.8 14.5 6.8 7.625 13.6 7.625V0.75C13.6 0.75 0 0.75 0 14.5ZM34 7.625V0.75C34 0.75 20.4 0.75 20.4 14.5V28.25H34V14.5H27.2C27.2 14.5 27.2 7.625 34 7.625Z"
                        fill="white" />
                </svg>
            </div>
            <p>{{ $quote['quote'] }}</p>
        </div>
        <div class="auth-quote-grid mt-5">
            <div class="col-start-2">
                <p>{{ $quote['author'] }}</p>
                <p class="mt-1 text-sm">{{ $quote['company'] }}</p>
            </div>
        </div>
    </div>
